package com.syntax.class03;

public class RecapOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Addition -> + 
		 * Subtraction -> - 
		 * Division -> /
		 * Remainder -> %
		 * 
		 */

		 int number1 = 10;       
		 int number2 =5;
		 System.out.println(number1/number2); // dividing number1 by number2
		 
		 double number3 = 9;    
		 double number4=2;
		 System.out.println(number3/number4); // we have change the data type to double
		  
		 double number5 = 1;      // we if we change to float we get more decimals 
		 double number6 = 3;
		 
		 int number7 = 9;
		 int number8 = 2;
		 System.out.println(" Answer = "+ number7/number8 );
		 System.out.println(" Remainder ="+ number7%number8 );
		
		  
		 
		 
	}

}
