package com.syntax.class6;

public class IncrementAndDecrment1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int num=90;
		num=num+1;
		System.out.println(num);
		//anothrt way to increment a variable by 1 ->++
		num++;
	System.out.println(num);
	
	int i =10;
	//i--;
	//i--;
	i--;
	i-=3;
	i+=9;
	i=i-1;
	/// 3 numbers will be deducted..
	System.out.println(i);
	//it should always be used to with variable 
	int a =100;
	a=a+20;
	System.out.println(a);
	}

}
